'use strict'
//
module.exports = (
    expireTime = {seconds: 0, minutes: 0, hours: 0, days: 0, days30: 0, years: 0},
    startDateMilliseconds = getToday()) => {
    //
    const ss = getSeconds(expireTime.seconds)
    const mm = getMinutes(expireTime.minutes)
    const hh = getHours(expireTime.hours)
    const da = getDays(expireTime.days)
    const mn = get30days(expireTime.days30)
    const yr = getYears(expireTime.years)
    //
    const default30days = Math.floor(startDateMilliseconds + (get30days(1)))
    let expiryTime = Math.floor(startDateMilliseconds + (ss + mm + hh + da + mn + yr))
    //
    if ((ss + mm + hh + da + mn + yr) === 0) expiryTime = default30days
    //
    return expiryTime
}
//
let getSeconds = (seconds = 0) => seconds * 1000
let getMinutes = (minutes = 0) => minutes * getSeconds(60)
let getHours = (hours = 0) => hours * getMinutes(60)
let getDays = (days = 0) => days * getHours(24)
let get30days = (months = 0) => months * getDays(30)
let getYears = (years = 0) => years * getDays(365)
let getToday = () => Date.now() / 1000
